mapper.select:
select DATE_FORMAT(addTime,'%Y-%m-%d') as addTime,count(*) as count from news
where 1 = 1

    and DATE_FORMAT(addTime,'%Y-%m-%d') BETWEEN #{qiantian} and #{today}


    and status = #{status}

group by DATE_FORMAT(addTime,'%Y-%m-%d')

SELECT DATE_FORMAT(time,'%Y-%m-%d') as time,COUNT(*) AS count FROM visit


        and DATE_FORMAT(time,'%Y-%m-%d') BETWEEN #{yesterday} AND #{today}


        and DATE_FORMAT(time,'%Y-%m') BETWEEN #{yesterday} AND #{today}


        and DATE_FORMAT(time,'%Y') BETWEEN #{yesterday} AND #{today}



    GROUP BY DATE_FORMAT(time,'%Y-%m-%d')


    GROUP BY DATE_FORMAT(time,'%Y-%m')


    GROUP BY DATE_FORMAT(time,'%Y')
